import React, { useState } from "react";
import Paragraph from "./Paragraph";
import { Container, Button, Form, Modal, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const DraftEditor = () => {
  const [paragraphs, setParagraphs] = useState([]);
  const [text, setText] = useState("");
  const [showModal, setShowModal] = useState(false);

  const addParagraph = () => {
    if (text.trim() === "") return;
    setParagraphs([...paragraphs, { id: Date.now(), text }]);
    setText("");
  };

  const saveDraft = () => {
    // Simula o salvamento e mostra o modal de sucesso
    console.log("Rascunho salvo:", paragraphs);
    setShowModal(true);

    // Fecha o modal automaticamente após 3 segundos
    setTimeout(() => {
      setShowModal(false);
    }, 3000);
  };

  return (
    <Container className="mt-4">
      <div className="d-flex align-items-center mb-3">
        <h2 className="mr-3">Editor de Redação</h2>
        <Button 
          variant="primary" 
          style={{ width: "200px" }} // Largura personalizada
          className="btn-sm"
          onClick={addParagraph}
        >
          Adicionar Parágrafo
        </Button>
      </div>

      <Form.Group>
        <Form.Control
          as="textarea"
          rows={3}
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Digite seu parágrafo..."
        />
      </Form.Group>

      <div className="mt-4">
        {/* Título Rascunho */}
        {paragraphs.length > 0 && (
          <h4 className="mt-4">Rascunho</h4>
        )}

        {/* Mapeia os parágrafos */}
        {paragraphs.map((p) => (
          <Paragraph key={p.id} text={p.text} />
        ))}

        {/* Botão "Salvar Rascunho" aparece somente se houver parágrafos */}
        {paragraphs.length > 0 && (
          <div className="d-flex align-items-center mt-3">
            <Button 
              variant="success" 
              style={{ width: "200px" }} // Largura personalizada
              className="btn-sm" 
              onClick={saveDraft}
            >
              Salvar Rascunho
            </Button>
          </div>
        )}
      </div>

      {/* Modal de Alerta */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Sucesso!</Modal.Title>
        </Modal.Header>
        <Modal.Body>Rascunho salvo com sucesso!</Modal.Body>
      </Modal>
    </Container>
  );
};

export default DraftEditor;
