import React, { useState } from "react";
import Paragraph from "./Paragraph";
import { Container, Button, Form, Modal } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const DraftEditor = () => {
  const [paragraphs, setParagraphs] = useState([]);
  const [text, setText] = useState("");
  const [showModal, setShowModal] = useState(false);

  const addParagraph = () => {
    if (text.trim() === "") return;
    setParagraphs([...paragraphs, { id: Date.now(), text }]);
    setText("");
  };

  const saveDraft = () => {
    // Simula o salvamento e mostra o modal de sucesso
    console.log("Rascunho salvo:", paragraphs);
    setShowModal(true);

    // Fecha o modal automaticamente após 3 segundos
    setTimeout(() => {
      setShowModal(false);
    }, 3000);
  };

  return (
    <Container className="mt-4">
      <h2 className="mb-3">Editor de Redação</h2>
      
      <Form.Group>
        <Form.Control
          as="textarea"
          rows={3}
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Digite seu parágrafo..."
        />
      </Form.Group>

      <Button 
        variant="primary" 
        className="mt-2 mr-2" 
        onClick={addParagraph}
      >
        Adicionar Parágrafo
      </Button>

      <Button 
        variant="success" 
        className="mt-2 ml-2" 
        onClick={saveDraft}
      >
        Salvar Rascunho
      </Button>

      <div className="mt-4">
        {paragraphs.map((p) => (
          <Paragraph key={p.id} text={p.text} />
        ))}
      </div>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Sucesso!</Modal.Title>
        </Modal.Header>
        <Modal.Body>Rascunho salvo com sucesso!</Modal.Body>
      </Modal>
    </Container>
  );
};

export default DraftEditor;
